import React, { Component }from 'react';
import AreasItem from '../areas-item';
import items from '../../data';

export default class AreasList extends Component {

    state = {
        areas: items.name,
        searchTerm: ''
    }

    changeSearchTerm = (e) => {
        this.setState({searchTerm: e.target.value});
    }

    dinamycSearch = () => {
        return this.state.areas.filter(area => area.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    }

    render() {

        const { areas } = this.props;

        const searchText = 'Type here to search your Area!';
        const searchStyle = {
            fontSize: '20px'
        };
        return (
            <section className="areas-block section">
                <div className="_container">
                    <div className="search-area">
                        <input placeholder={ searchText } style={ searchStyle } className="foo" value={ this.state.searchTerm }
                                />
                    </div>
                    <div className="areas-block__slides">
                        <ul className="slides">
                            <li>
                                <div className="row">
                                    {areas.map(item => {
                                        return <AreasItem key={item.id} area={item} />;
                                    })}
                                </div>
                            </li>
                        </ul>
                    </div>
                </div>
            </section>
        );
    }
}
